def time_func(arg):
    now = datetime.utcnow()
    time = now - datetime.strptime(arg, '%Y-%m-%d %H:%M:%S.%f+00:00')
    if time.seconds < 86400 and time.days == 0:
        if time.seconds == 1: 
            return "1 second ago"
        elif time.seconds < 60:
            return f'{time.seconds} seconds ago'
        elif time.seconds < 120:
            return "1 minute ago"

